mapper.insert:
insert into
member(
	  mid
	, mpw
	, mnm
	, muuid
	, mgender
	, mmanyn
	, mphone
	, memail
)
value(
	  #{mid}
	, #{mpw}
	, #{mnm}
	, #{muuid}
	, #{mgender}
	, 2
	, #{mphone}
	, #{memail}
)

mapper.update:
update
	member
set
	  mpw = #{mpw}
	, mnm = #{mnm}
	, mgender = #{mgender}
	, mphone = #{mphone}
	, memail = #{memail}
where
	1 = 1
and
	mid = #{mid}
and
	muuid = #{muuid}

mapper.delete:
delete
  from mypage
 where mid = #{mid}
   and hno = #{hno}

mapper.select:
select count(*)
  from mypage
 where mid = #{mid}
   and hno = #{hno}
